import React from "react";
import Card from "react-bootstrap/Card";

const Tweet = ({ text, author, date }) => {
  return (
    <div>
      <Card
        style={{
          border: "2px solid white",
          margin: "30px auto 30px auto",
          width: "30rem",
          padding: "5px",
          color: "white",
        }}
      >
        <Card.Body>
          <Card.Title>{text.toUpperCase()}</Card.Title>
          <Card.Text>
            by {author} on {date}
          </Card.Text>
        </Card.Body>
      </Card>
    </div>
  );
};

export default Tweet;
