import Tweet from "./components/Tweet";
import axios from "axios";
import { useEffect, useState } from "react";

function App() {
  const [tweets, setTweets] = useState([]);

  useEffect(() => {
    fetchTweets();
  });

  const fetchTweets = () => {
    axios
      .get(`https://coursera-twitter-api.herokuapp.com/tweets`)
      .then((data) => {
        console.log(data);
        setTweets(...tweets, data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <div className="App">
      {tweets.map((tweet, id) => {
        id = tweet.id;

        return <Tweet {...tweet} />;
      })}
      <Tweet text="Text passed down" author="Ijezie Daniel" Date="2020-20-15" />
    </div>
  );
}

export default App;
