import React, { useState } from "react";

const Tweet = ({ text, author, date }) => {
  const [counter, setCounter] = useState(0);
  const increament = () => {
    setCounter(counter + 1);
  };
  const decreament = () => {
    setCounter(counter - 1);
  };
  return (
    <div>
      <h3>{text.toUpperCase()}</h3>
      <h6>
        by {author} on {date}
      </h6>
    </div>
  );
};

export default Tweet;
